import EditorJS from '@editorjs/editorjs';
import Header from '@editorjs/header';
import List from '@editorjs/list';
import Checklist from '@editorjs/checklist';
import Link from '@editorjs/link';
import SimpleImage from '@editorjs/simple-image';

const getEditorId = function(){
  return 'knowledge-editor';
}

if ( hasElem('.editorjs') ){
  let holder = getEditorId();

  if ( holder === 'knowledge-editor' ){ //This is an article
    let data = JSON.parse(document.querySelector('#id-field').value) || {};
    createArticleEditor(holder, data);
  }
}


function createArticleEditor(holder, data){
  const editor = new EditorJS({
    holder,
    tools: {
      title: {
        class: Header,
        toolbox: {
          title: "Title",
          icon: '<svg width="17" height="15" version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 25.531 25.531" style="enable-background:new 0 0 25.531 25.531;" xml:space="preserve"><g><g id="c179_text"><path d="M25.198,6.273c-0.014,0.23-0.045,0.389-0.087,0.467c-0.045,0.084-0.176,0.145-0.392,0.183 c-0.469,0.104-0.781-0.074-0.935-0.533C23.239,4.7,22.59,3.578,21.84,3.016c-1.041-0.773-2.862-1.161-5.469-1.161 c-1.054,0-1.633,0.115-1.734,0.343c-0.036,0.075-0.057,0.184-0.057,0.324v18.999c0,0.812,0.188,1.383,0.571,1.709 c0.382,0.32,1.069,0.731,2.201,0.999c0.483,0.103,0.97,0.2,1.034,0.239c0.46,0,0.504,1.057-0.376,1.057 c-0.025,0.016-10.375-0.008-10.375-0.008s-0.723-0.439-0.074-1.023c0.271-0.121,0.767-0.343,0.767-0.343s1.83-0.614,2.211-1.009 c0.434-0.445,0.648-1.164,0.648-2.154V2.521c0-0.369-0.229-0.585-0.687-0.647c-0.049-0.015-0.425-0.02-1.122-0.02 c-2.415,0-4.191,0.418-5.338,1.259C3.176,3.735,2.411,4.877,1.737,6.545C1.52,7.065,1.22,7.234,0.84,7.058 C0.408,6.957,0.251,6.719,0.363,6.353c0.445-1.374,0.668-3.31,0.668-5.814c0-0.292,0.387-0.586,1.163-0.533L23.56,0.064 c0.709-0.104,1.096,0.012,1.16,0.343C25.076,2.096,25.234,4.052,25.198,6.273z"/></g><g id="Capa_1_282_"></g></g></svg>'
        },
        config: {
          placeholder: 'Enter a title',
          levels: [1],
          defaultLevel: 1
        },

      },
      header: {
        class: Header,
        inlineToolbar: ['link']
      },
      list: {
        class: List,
        inlineToolbar: true
      }
    },
    data,
    onReady: () => {
      //console.log('Editor.js is ready to work!')
      editor.focus();
   }
  });

  function saveKnowledge(){
    editor.save().then(data=>{
      let url = document.querySelector('#id-field').getAttribute('data-url');
      let hasTitle = data.blocks[0].type === 'title'
      if (!hasTitle){
        data.blocks.unshift({type: 'title', data: {text: 'Title is required for Knowledge article', level: 1}});
        editor.destroy();
        document.querySelector('#save-knowledge').removeEventListener('click', saveKnowledge)
        createArticleEditor(holder, data);
        console.log('Title required in position 0!');
      }
      else {
        data.blocks.forEach((block, i)=>{
          if (block.type === 'title' && i > 0 ){
            block.type = 'header';
            console.log('Title only available in position 0, changed block to header!');
          }
        });
        console.log({url, data, hasTitle});
        postData(url, data);
      }
    });
  }

  document.querySelector('#save-knowledge').addEventListener('click', saveKnowledge);
}

async function postData(url = '', data = {}) {
  try {
    const response = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    });
    if ( response.status === 200 ){
      window.location.href = response.url;
    }
    else {
      console.log(response); 
    }

  } catch (e) {
    console.log('Something went wrong', e);
  }
}
